const eaccount = $('Set Data from Email').first().json.fromMail;
const reply_to_uuid = $('Set Data from Email').first().json.UUID;
const subject = $('Extract UUID').first().json.items[0].subject;
const messageReplyTemp = $('Reply Extraction').first().json.aiGeneratedReply;
const messageReply = messageReplyTemp.replace(/\n/g,"<br>")
const originalBodyTemp = $('Set Data from Email').first().json.Sent;
const originalBody = originalBodyTemp.replace(/\n/g,"<br>")
const meetingLink = $('Set Data from Email').first().json.meetingLink
const hasMeetingLinkInReply = meetingLink && messageReply.includes(meetingLink);
const cc = $('Set Data from Email').first().json.CC
const bcc = $('Set Data from Email').first().json.BCC

return [
  {
    eaccount,
    reply_to_uuid,
    subject,
    body: {
      html: messageReply, //+ "<br>" + originalBody,
    },
    cc_address_email_list: cc,
    bcc_address_email_list: bcc,
  },
];

